package programs;

public class Swappingwithoutvariable {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

	    
	        int x = 100;
	        int y = 200;
	 
	        
	        x = x + y;     
	        y = x - y;      
	        x = x - y;     
	         
	        
	        System.out.println("x = " + x + " and y = " + y);
	    }
	}

